mapper.select:
SELECT plan.plan_id, plan.plan_date, plan.plan_index, plan.planner_id 
FROM plan 
WHERE plan.planner_id = #{plannerId}
ORDER BY plan_index;

mapper.update:
UPDATE plan 
SET 
	plan.plan_date = #{planDto.planDate}, 
	plan.plan_index = #{planDto.planIndex} 
WHERE plan.plan_id = #{planId};
